explain SELECT ri.REST_ID, ri.REST_NAME, ri.FOOD_TYPE, ri.FAVORITES, ri.ADDRESS, ROUND(avg(rr.REVIEW_SCORE), 2) as SCROE FROM REST_INFO ri JOIN REST_REVIEW rr ON ri.REST_ID = rr.REST_ID WHERE ri.ADDRESS like "서울%" GROUP BY ri.REST_ID ORDER BY  ROUND(avg(rr.REVIEW_SCORE), 2) DESC, ri.FAVORITES DESC